custom_css.gtk4:
/* Window controls - "Alpha Tritanopia" used as template */

windowcontrols > button {
	color: transparent;
	min-width: 25px;
	min-height: 25px;
	border-radius: 100%;
	padding: 0;
	margin: 4px;
}

windowcontrols > button > image {
	padding: 5px;
}

button.titlebutton.close,
windowcontrols > button.close {
	color: rgba(255, 255, 255, 0.35);
	background-color: #505050;
}

button.titlebutton.close:hover,
windowcontrols > button.close:hover {
	color: transparent;
	background-color: @warning_color;
}

button.titlebutton.maximize,
windowcontrols > button.maximize {
	color: rgba(255, 255, 255, 0.35);
	background-color: #505050;
}

button.titlebutton.maximize:hover,
windowcontrols > button.maximize:hover {
	color: transparent;
	background-color: @accent_color;
}

button.titlebutton.minimize,
windowcontrols > button.minimize {
	color: rgba(255, 255, 255, 0.35);
	background-color: #505050;
	margin-left: 12px;
}

button.titlebutton.minimize:hover,
windowcontrols > button.minimize:hover {
	color: transparent;
	background-color: @success_color;
}

/* Control widgets - "Pop Dark" used as template */

switch slider {
	background-color: #363636;
}

/* Viewswitcher - "Pop Dark" used as template */

viewswitcher button.flat.horizontal.toggle {
	margin-top: 0px;
	margin-bottom: 0px;
	border-radius: 0px;
}

viewswitcher button.flat.horizontal.toggle:hover {
	margin-top: 0px;
	margin-bottom: 0px;
	border-radius: 0px;
	box-shadow: inset 0px -4px darker(@headerbar_bg_color);
}

viewswitcher button.flat.horizontal.toggle:checked {
	margin-top: 0px;
	margin-bottom: 0px;
	border-radius: 0px;
	box-shadow: inset 0px -4px @accent_bg_color;
}

/* Viewswitcher bottom - "Pop Dark" used as template */
revealer viewswitcher.narrow button.flat.vertical.toggle {
	margin-bottom: 0px;
	border-radius: 0px;
}

revealer viewswitcher.narrow button.flat.vertical.toggle:hover {
	margin-bottom: 0px;
	border-radius: 0px;
	box-shadow: inset 0px 4px darker(@headerbar_bg_color);
}

revealer viewswitcher.narrow button.flat.vertical.toggle:checked {
	margin-bottom: 0px;
	border-radius: 0px;
	box-shadow: inset 0px 4px @accent_bg_color;
}
