custom_css.gtk4:
decoration {
  box-shadow: 0 3px 12px 1px rgba(0, 0, 0, 0.7), 0 0 0 1px shade(@headerbar_bg_color,1.3);
}

decoration:backdrop {
  box-shadow: 0 3px 12px 1px transparent, 0 2px 6px 2px rgba(0, 0, 0, 0.4), 0 0 0 1px shade(@headerbar_bg_color,1.1);
}

.tiled decoration, .tiled-top decoration, .tiled-bottom decoration, .tiled-right decoration, .tiled-left decoration {
  box-shadow: 0 0 0 1px shade(@headerbar_bg_color,1.1), 0 0 0 20px transparent;
}

messagedialog.csd decoration, .csd.popup decoration, .maximized .csd.popup decoration {
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.8), 0 0 0 1px alpha(shade(@headerbar_bg_color,1.3),0.9);
}

.maximized .csd.popup decoration {
  border-radius: 6px;
}

.maximized decoration, .fullscreen decoration {
  border-radius: 0;
  box-shadow: none;
}

.ssd decoration {
  box-shadow: 0 0 0 1px shade(@headerbar_bg_color,1.3);
}

.ssd decoration:backdrop {
  box-shadow: 0 0 0 1px shade(@headerbar_bg_color,1.1);
}

.ssd.maximized decoration, .ssd.maximized decoration:backdrop {
  box-shadow: none;
}

.solid-csd decoration {
  box-shadow: inset 0 0 0 5px alpha(currentColor,0.5), inset 0 0 0 4px @headerbar_bg_color, inset 0 0 0 1px alpha(currentColor,0.5);
}

.solid-csd decoration:backdrop {
  box-shadow: inset 0 0 0 3px @window_bg_color;
}

.titlebar, headerbar {
  border-bottom-color: alpha(currentColor,0.15);
}

.titlebar:backdrop, headerbar:backdrop {
  border-bottom-color: alpha(currentColor,0.2);
}

button.titlebutton,
windowcontrols > button {
  color: transparent;
  min-width: 18px;
  min-height: 18px;
}

button.titlebutton:backdrop {
  opacity: 0.5;
}

windowcontrols > button {
  border-radius: 100%;
  padding: 0;
  margin: 0 5px;
}

windowcontrols > button > image {
  padding: 0;
}

button.titlebutton.close, button.titlebutton.close:hover:backdrop,
windowcontrols > button.close,
windowcontrols > button.close:hover:backdrop {
  background-color: #ff267c;
}

button.titlebutton.close:hover,
windowcontrols > button.close:hover {
  background-color: shade(#ff267c,1.3);
}

button.titlebutton.maximize, button.titlebutton.maximize:hover:backdrop,
windowcontrols > button.maximize,
windowcontrols > button.maximize:hover:backdrop {
  background-color: #00ff02;
}

button.titlebutton.maximize:hover,
windowcontrols > button.maximize:hover {
  background-color: shade(#00ff02,1.3);
}

button.titlebutton.minimize, button.titlebutton.minimize:hover:backdrop,
windowcontrols > button.minimize,
windowcontrols > button.minimize:hover:backdrop {
  background-color: #ffcc00;
}

button.titlebutton.minimize:hover,
windowcontrols > button.minimize:hover {
  background-color: shade(#ffcc00,1.3);
}

notebook > header tab:not(:backdrop):checked.reorderable-page,
tabbar tab:not(:backdrop):checked {
  background-color: @headerbar_bg_color;
}

.nautilus-window .sidebar {
  background-color: @view_bg_color;
}

.nautilus-window .sidebar:backdrop {
  background-color: @window_bg_color;
}

custom_css.gtk3:
.terminal-window.root headerbar button:not(.titlebutton),
.terminal-window.remote headerbar button:not(.titlebutton) {
  min-width: 16px;
  background: transparent;
  border: 1px solid transparent;
}

.terminal-window.root headerbar button:not(.titlebutton):hover,
.terminal-window.remote headerbar button:not(.titlebutton):hover,
.terminal-window.root headerbar button:not(.titlebutton):checked,
.terminal-window.remote headerbar button:not(.titlebutton):checked,
.terminal-window.root headerbar button.titlebutton,
.terminal-window.remote headerbar button.titlebutton {
  background: alpha(white, 0.12);
}

.terminal-window.root headerbar button:not(.titlebutton):active,
.terminal-window.remote headerbar button:not(.titlebutton):active,
.terminal-window.root headerbar button:not(.titlebutton):checked:hover,
.terminal-window.remote headerbar button:not(.titlebutton):checked:hover,
.terminal-window.root headerbar button.titlebutton:hover,
.terminal-window.remote headerbar button.titlebutton:hover {
  background: alpha(white, 0.18);
}

.terminal-window.root headerbar button.titlebutton:active,
.terminal-window.remote headerbar button.titlebutton:active {
  background: alpha(white, 0.3);
}

.csd.popup decoration {  
border-radius: 12px; 
box-shadow: 0 2px 2.4px -1px rgba(0, 0, 0, 0.2), 0 4px 3px 0 rgba(0, 0, 0, 0.14), 0 1px 6px 0 rgba(0, 0, 0, 0.12);
}


menu, .menu, .context-menu { 
  margin: 4px; 
  padding: 6px 6px; 
}

.csd menu, .csd .menu, .csd .context-menu { 
  border: none; 
  border-radius: 12px; 
}

menu menuitem, .menu menuitem, .context-menu menuitem {
  margin: 0;
  border-radius: 6px;
  padding: 7px 6px;
}

menu separator {margin: 6px 0}

menubar menu, wxMenu   {
  padding: 6px;
  border-radius: 12px
}

menu menuitem arrow, .menu menuitem arrow, .context-menu menuitem arrow {
color: #c2c2c2;
}

